import { Component } from 'react';

import logo from './logo.svg';
import './App.css';

class App extends Component {
  constructor() {
    super();
    this.state = {
      name: 'Mavis',
      school: "Home"
    };
  };

  render() {
    return (
      <div className="App">
        <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
          <p>
            Hello my name is {this.state.name}, I study at {this.state.school}.
          </p>
          <button onClick={() => {
            this.setState(
              () => { // state setter
                return {name: 'Mars', school: "FDU"};
              }, 
              () => { // callback function
                console.log(this.state)
              });
          }}>
            Change Name
          </button>
        </header>
      </div>
    );
  };
}

export default App;
